// SPDX-License-Identifier: GPL-3.0

pragma solidity ^0.8.0;
import "@openzeppelin/contracts/token/ERC1155/utils/ERC1155Holder.sol";

// import "https://github.com/OpenZeppelin/openzeppelin-contracts/blob/master/contracts/token/ERC1155/IERC1155.sol";
interface IERC721 {
    function transferFrom(
        address from,
        address to,
        uint256 tokenId
    ) external;

    function safeTransferFrom(
        address from,
        address to,
        uint256 tokenId,
        bytes memory _data
    ) external;
}

interface IERC1155 {
    function safeTransferFrom(
        address from,
        address to,
        uint256 id,
        uint256 amount,
        bytes calldata data
    ) external;
}

contract NFT_ice is ERC1155Holder {
    bytes32 private constant _PREFIX = "\x19Ethereum Signed Message:\n32";
    // user to discord to tokenAddress to total_tokens_staked

    mapping(address => mapping(string => mapping(address => uint256)))
        public nftStakes721;
    mapping(address => mapping(string => mapping(uint256 => uint256)))
        public nftStakes1155;

    // mapping(address=>mapping(address=>uint)) public balances;
    // owner
    address payable public owner;
    // fee
    uint256 public perNFTStakingFee= 2000000000; // 2gwei fee per NFT transfer
    // events
    event StakedERC721(
        address indexed staker,
        string indexed discordId,
        address indexed tokenAddress,
        uint256  tokenId
    );
    event StakedERC1155(
        address indexed staker,
        string indexed discordId,
        address indexed tokenAddress,
        uint256  collectionId
    );

    event UnstakedERC721(
        address indexed staker,
        string indexed discordId,
        address indexed tokenAddress,
        uint256  tokenId
    );
    event UnstakedERC1155(
        address indexed staker,
        string indexed discordId,
        address indexed tokenAddress,
        uint256  collectionId
    );

    event FeeUpdate(uint256 indexed newFee);
    // Wrapping every NFT transfer detail in a single transaction object
    // to save gas and give better UX
    struct UserTransaction {
        address the_staker;
        address[] erc721Contracts;
        uint256[] erc721TokenIds;
        address[] erc1155Contracts;
        uint256[] erc1155CollectionIds;
        uint256 nonce;
        bytes personSignature;
        bytes platformSignature;
        uint256 discount;
        string discordId;
    }

    constructor(address owner_) payable {
        owner = payable(owner_);
    }

    modifier onlyOwner() {
        require(
            msg.sender == owner,
            "only owner is allowed to call this function"
        );
        _;
    }

    /********************* Signature Validation **********************/

    modifier validateSignatures(
        address theStaker,
        uint256 nonce,
        bytes memory userSig,
        bytes memory platformSig
    ) {
        bytes32 message = prefixed(
            keccak256(abi.encodePacked(theStaker, nonce))
        );
        address signerAddress = recoverSigner(message, userSig);
        require(signerAddress == theStaker, "Invalid user signature");

        signerAddress = recoverSigner(message, platformSig);
        require(signerAddress == owner, "Invalid platform signature");
        _;
    }

    function prefixed(bytes32 hash) internal pure returns (bytes32) {
        return
            keccak256(
                abi.encodePacked("\x19Ethereum Signed Message:\n32", hash)
            );
    }

    function recoverSigner(bytes32 message, bytes memory sig)
        internal
        pure
        returns (address)
    {
        uint8 v;
        bytes32 r;
        bytes32 s;

        (v, r, s) = splitSignature(sig);

        return ecrecover(message, v, r, s);
    }

    function splitSignature(bytes memory sig)
        internal
        pure
        returns (
            uint8,
            bytes32,
            bytes32
        )
    {
        require(sig.length == 65);

        bytes32 r;
        bytes32 s;
        uint8 v;

        assembly {
            // first 32 bytes, after the length prefix
            r := mload(add(sig, 32))
            // second 32 bytes
            s := mload(add(sig, 64))
            // final byte (first byte of the next 32 bytes)
            v := byte(0, mload(add(sig, 96)))
        }

        return (v, r, s);
    }

    /*******************Staking Starts here *************************/

    function stakeERC721(
        address the_staker,
        address[] memory erc721Contracts,
        uint256[] memory tokenIds,
        string memory discordId
    ) internal {
        for (uint256 i = 0; i < erc721Contracts.length; i++) {
            IERC721(erc721Contracts[i]).transferFrom(
                the_staker,
                address(this),
                tokenIds[i]
            );
            nftStakes721[the_staker][discordId][erc721Contracts[i]] += 1;
            emit StakedERC721(
                the_staker,
                discordId,
                erc721Contracts[i],
                tokenIds[i]
            );
        }
    }

    function stakeERC1155(
        address the_staker,
        address[] memory erc1155Contracts,
        uint256[] memory collectionIds,
        string memory discordId
    ) internal {
        for (uint256 i = 0; i < erc1155Contracts.length; i++) {
            IERC1155(erc1155Contracts[i]).safeTransferFrom(
                the_staker,
                address(this),
                collectionIds[i],
                1,
                ""
            );
            nftStakes1155[the_staker][discordId][collectionIds[i]] += 1;
            emit StakedERC1155(
                the_staker,
                discordId,
                erc1155Contracts[i],
                collectionIds[i]
            );
        }
    }

    function getDiscountFee(UserTransaction memory transaction)public view returns(uint256) {
                // total fee to pay in general
        uint256 stakingFee = (perNFTStakingFee* (transaction.erc1155CollectionIds.length + transaction.erc721Contracts.length) );
        // if we get percentage of dsicount , let's calculate
        uint256 discount = (stakingFee* (transaction.discount)) / (100);
        // final discounted price
        return   stakingFee - discount;
       
    }
    function stakeNFTs(UserTransaction memory transaction)
        public
        payable
        validateSignatures(
            transaction.the_staker,
            transaction.nonce,
            transaction.personSignature,
            transaction.platformSignature
        )
    {
        uint priceToPay=getDiscountFee(transaction);
        require(msg.value >= priceToPay, "Insufficient fee sent to platform !");

        stakeERC721(
            transaction.the_staker,
            transaction.erc721Contracts,
            transaction.erc721TokenIds,
            transaction.discordId
        );
        stakeERC1155(
            transaction.the_staker,
            transaction.erc1155Contracts,
            transaction.erc1155CollectionIds,
            transaction.discordId
        );
    }

    /****************** Unstaking starts here ************************ */

    function unstakeERC721(
        address the_staker,
        address[] memory erc721Contracts,
        uint256[] memory tokenIds
    ) internal {}

    function unstakeERC1155(
        address the_staker,
        address[] memory erc1155Contracts,
        uint256[] memory collectionIds
    ) internal {}

    function unstakeNFTs(UserTransaction memory transaction)
        public
        validateSignatures(
            transaction.the_staker,
            transaction.nonce,
            transaction.personSignature,
            transaction.platformSignature
        )
    {
        for (uint256 i = 0; i < transaction.erc721Contracts.length; i++) {
            if (
                nftStakes721[transaction.the_staker][transaction.discordId][
                    transaction.erc721Contracts[i]
                ] > 0
            ) // if user has staked more than one staking balance in the target contract ,
            // then he potentially has staked this token too ,
            // because there is no other way to modify this mapping

            {
                IERC721(transaction.erc721Contracts[i]).transferFrom(
                    address(this),
                    transaction.the_staker,
                    transaction.erc721TokenIds[i]
                );

                nftStakes721[transaction.the_staker][transaction.discordId][
                    transaction.erc721Contracts[i]
                ] -= 1;

                emit UnstakedERC721(
                    transaction.the_staker,
                     transaction.discordId,
                    transaction.erc721Contracts[i],
                    transaction.erc721TokenIds[i]
                   
                );
            }
        }
        for (uint256 i = 0; i < transaction.erc1155Contracts.length; i++) {
            if (
                nftStakes1155[transaction.the_staker][transaction.discordId][
                    transaction.erc1155CollectionIds[i]
                ] > 0
            ) {
                IERC1155(transaction.erc1155Contracts[i]).safeTransferFrom(
                    address(this),
                    transaction.the_staker,
                    transaction.erc1155CollectionIds[i],
                    1,
                    ""
                );
                nftStakes1155[transaction.the_staker][transaction.discordId][
                    transaction.erc1155CollectionIds[i]
                ] -= 1;
                emit UnstakedERC1155(
                    transaction.the_staker,
                     transaction.discordId,
                    transaction.erc1155Contracts[i],
                    transaction.erc1155CollectionIds[i]
                    
                );
            }
        }
    }

    /***  */
    function updateFee(uint256 newFee) public {
        require(msg.sender == owner, "only owner can update fee.");
        perNFTStakingFee= newFee;
        emit FeeUpdate(newFee);
    }
}
